import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Login.css';

function Categories() {
  const [categories, setCategories] = useState([]);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  // Récupère les catégories et produits au chargement
  useEffect(() => {
    const fetchData = async () => {
      try {
        // Récupère les catégories
        const catRes = await fetch('http://localhost:5000/api/v1/categories');
        const catData = await catRes.json();
        setCategories(catData);

        // Récupère tous les produits
        const prodRes = await fetch('http://localhost:5000/api/v1/products');
        const prodData = await prodRes.json();
        setProducts(prodData);

        setLoading(false);
      } catch (err) {
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  // Regroupe les produits par catégorie
  const productsByCategory = {};
  products.forEach(product => {
    const catId = product.category?._id || product.category;
    if (!productsByCategory[catId]) productsByCategory[catId] = [];
    productsByCategory[catId].push(product);
  });

  if (loading) return <div>Chargement...</div>;

  return (
    <div className="categories-bg">
      <h1>Nos Catégories Solidaires</h1>
      {categories.map(cat => (
        <section className="category" key={cat._id}>
          <h2>{cat.icon ? `${cat.icon} ` : ''}{cat.name}</h2>
          <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center' }}>
            {(productsByCategory[cat._id] || []).map(product => (
              <div className="product" key={product._id}>
                <img
                  src={product.image}
                  alt={product.name}
                  style={{ width: '100%', borderRadius: 5 }}
                  onError={e => { e.target.src = '/logo192.png'; }}
                />
                <h3>{product.name}</h3>
                <p>{product.description}</p>
                <p className="price">Prix : {product.price} DH</p>
                <span className="favori"><i className="fas fa-heart"></i></span>
                <button className="add-to-cart" data-price={product.price}>
                  Donner ce produit
                </button>
              </div>
            ))}
          </div>
        </section>
      ))}
      <button onClick={() => navigate('/accueil')}>Retour à l'accueil</button>
    </div>
  );
}

export default Categories;
