import React, { useEffect, useState } from 'react';
import './Categories.css';

function Categories() {
  const [categories, setCategories] = useState([]);
  const [productsByCategory, setProductsByCategory] = useState({});
  const [loading, setLoading] = useState(true);

  // Panier et favoris
  const [cart, setCart] = useState([]);
  const [cartOpen, setCartOpen] = useState(false);
  const [favorites, setFavorites] = useState([]);
  const [favoritesOpen, setFavoritesOpen] = useState(false);

  // Récupère les catégories et produits
  useEffect(() => {
    fetch('http://localhost:5000/api/v1/categories')
      .then(res => res.json())
      .then(data => {
        setCategories(data);
        setLoading(false);
        data.forEach(cat => {
          fetch(`http://localhost:5000/api/v1/products?categories=${cat._id}`)
            .then(res => res.json())
            .then(products => {
              setProductsByCategory(prev => ({
                ...prev,
                [cat._id]: products
              }));
            });
        });
      });
  }, []);

  // Ajout au panier
  const addToCart = (product) => {
    setCart(prev => {
      const found = prev.find(item => item._id === product._id);
      if (found) {
        return prev.map(item =>
          item._id === product._id ? { ...item, qty: item.qty + 1 } : item
        );
      }
      return [...prev, { ...product, qty: 1 }];
    });
    setCartOpen(true);
  };

  // Ajout/suppression favoris
  const toggleFavorite = (product) => {
    setFavorites(prev => {
      if (prev.find(item => item._id === product._id)) {
        return prev.filter(item => item._id !== product._id);
      }
      return [...prev, product];
    });
    setFavoritesOpen(true);
  };

  // Suppression panier
  const removeFromCart = (productId) => {
    setCart(prev => prev.filter(item => item._id !== productId));
  };

  // Suppression favoris
  const removeFromFavorites = (productId) => {
    setFavorites(prev => prev.filter(item => item._id !== productId));
  };

  // Vider panier/favoris
  const clearCart = () => setCart([]);
  const clearFavorites = () => setFavorites([]);

  // Total panier
  const cartTotal = cart.reduce((sum, item) => sum + (item.price * item.qty), 0);

  if (loading) return <div>Chargement...</div>;

  return (
    <div className="categories-bg">
      {/* Icône favoris */}
      <div id="favorites-icon" onClick={() => setFavoritesOpen(true)}>
        <i className="fas fa-heart" data-count={favorites.length}></i>
      </div>
      {/* Sidebar favoris */}
      <div id="favorites-sidebar" className={favoritesOpen ? 'active' : ''}>
        <div className="favorites-header">
          <h3>Vos Favoris ❤️</h3>
          <button id="close-favorites" onClick={() => setFavoritesOpen(false)}>&times;</button>
        </div>
        <div className="favorites-items">
          {favorites.length === 0 ? (
            <div className="empty-favorites">Aucun favori</div>
          ) : (
            favorites.map(item => (
              <div className="favorite-item" key={item._id}>
                <span>{item.name}</span>
                <button onClick={() => removeFromFavorites(item._id)} style={{color: 'red', border: 'none', background: 'none', fontSize: 18, cursor: 'pointer'}}>×</button>
              </div>
            ))
          )}
        </div>
        <button id="clear-favorites" onClick={clearFavorites}>Vider</button>
      </div>

      {/* Icône panier */}
      <div id="cart-icon" onClick={() => setCartOpen(true)}>
        <i className="fas fa-shopping-cart" data-count={cart.length}></i>
      </div>
      {/* Sidebar panier */}
      <div id="cart-sidebar" className={cartOpen ? 'active' : ''}>
        <div className="cart-header">
          <h3>Votre Panier</h3>
          <button id="close-cart" onClick={() => setCartOpen(false)}>&times;</button>
        </div>
        <div className="cart-items">
          {cart.length === 0 ? (
            <div className="empty-cart">Panier vide</div>
          ) : (
            cart.map(item => (
              <div className="cart-item" key={item._id}>
                <span>{item.name} x{item.qty}</span>
                <button onClick={() => removeFromCart(item._id)} style={{color: 'red', border: 'none', background: 'none', fontSize: 18, cursor: 'pointer'}}>×</button>
              </div>
            ))
          )}
        </div>
        <div className="cart-total">Total: <span id="cart-total">{cartTotal}</span> DH</div>
        <button id="validate-cart" onClick={() => alert('Merci pour votre don !')}>Valider</button>
        <button id="clear-favorites" style={{background:'#ff6b6b', marginTop: 8}} onClick={clearCart}>Vider le panier</button>
      </div>

      <h1>Nos Catégories Solidaires</h1>
      {categories.map(cat => (
        <section className="category" key={cat._id}>
          <h2>{cat.icon ? <span>{cat.icon} </span> : null}{cat.name}</h2>
          <div className="products-row">
            {(productsByCategory[cat._id] || []).map(product => (
              <div className="product" key={product._id}>
                <img
                  src={product.image}
                  alt={product.name}
                  onError={e => { e.target.src = '/logo192.png'; }}
                />
                <h3>{product.name}</h3>
                <p>{product.description}</p>
                <p className="price">Prix : {product.price} DH</p>
                <span
                  className={`favori${favorites.find(f => f._id === product._id) ? ' active' : ''}`}
                  onClick={() => toggleFavorite(product)}
                  title="Ajouter/Retirer des favoris"
                >
                  <i className="fas fa-heart"></i>
                </span>
                <button className="add-to-cart" data-price={product.price} onClick={() => addToCart(product)}>
                  Donner ce produit
                </button>
              </div>
            ))}
          </div>
        </section>
      ))}
    </div>
  );
}

export default Categories;
