import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Login.css';

function Login() {
  const [showRegister, setShowRegister] = useState(false);
  const [registerData, setRegisterData] = useState({
    name: '',
    email: '',
    password: '',
    phone: '',
    street: '',
    zip: '',
    city: '',
    country: ''
  });
  const [registerError, setRegisterError] = useState('');
  const [loginData, setLoginData] = useState({
    email: '',
    password: ''
  });
  const [loginError, setLoginError] = useState('');
  const navigate = useNavigate();

  // Pour afficher un message lors de la crÃ©ation de compte
  const handleRegister = (e) => {
    e.preventDefault();
    alert("ðŸ‘‹ Bienvenue ! Votre compte a Ã©tÃ© crÃ©Ã© avec succÃ¨s !");
    setShowRegister(false);
  };

  // Pour afficher/masquer le mot de passe
  const togglePassword = (e) => {
    const input = e.target.previousSibling;
    input.type = input.type === 'password' ? 'text' : 'password';
    e.target.classList.toggle('fa-eye-slash');
  };

  return (
    <div
      style={{
        minHeight: "100vh",
        background: `url(${process.env.PUBLIC_URL + '/the.png'}) center/cover no-repeat`
      }}
    >
      <div className="welcome-message">
        <p>Chaque connexion commence par</p>
        <p>un sourire et une tasse de thÃ©.</p>
      </div>

      <div className="login-page">
        <div className="form">
          {/* Formulaire d'inscription */}
          {showRegister ? (
            <form className="register-form" onSubmit={async (e) => {
              e.preventDefault();
              setRegisterError('');
              try {
                const res = await fetch('http://localhost:5000/api/v1/users/register', {
                  method: 'POST',
                  headers: { 'Content-Type': 'application/json' },
                  body: JSON.stringify(registerData)
                });
                if (res.ok) {
                  alert("ðŸ‘‹ Bienvenue ! Votre compte a Ã©tÃ© crÃ©Ã© avec succÃ¨s !");
                  setShowRegister(false);
                } else {
                  const data = await res.json();
                  setRegisterError(data.message || "Erreur lors de l'inscription");
                }
              } catch {
                setRegisterError("Erreur serveur");
              }
            }}>
              <input type="text" required placeholder="nom"
                value={registerData.name}
                onChange={e => setRegisterData({ ...registerData, name: e.target.value })} />
              <input type="text" required placeholder="adresse e-mail"
                value={registerData.email}
                onChange={e => setRegisterData({ ...registerData, email: e.target.value })} />
              <input type="password" required placeholder="mot de passe"
                value={registerData.password}
                onChange={e => setRegisterData({ ...registerData, password: e.target.value })} />
              <input type="tel" required placeholder="numÃ©ro de tÃ©lÃ©phone"
                value={registerData.phone}
                onChange={e => setRegisterData({ ...registerData, phone: e.target.value })} />
              <input type="text" name="street" placeholder="Adresse" required
                value={registerData.street}
                onChange={e => setRegisterData({ ...registerData, street: e.target.value })} />
              <input type="text" name="zip" placeholder="Code postal" required
                value={registerData.zip}
                onChange={e => setRegisterData({ ...registerData, zip: e.target.value })} />
              <input type="text" name="city" placeholder="Ville" required
                value={registerData.city}
                onChange={e => setRegisterData({ ...registerData, city: e.target.value })} />
              <input type="text" name="country" placeholder="Pays" required
                value={registerData.country}
                onChange={e => setRegisterData({ ...registerData, country: e.target.value })} />
              <button type="submit">crÃ©er</button>
              {registerError && <div style={{ color: 'red' }}>{registerError}</div>}
              <p className="message">
                DÃ©jÃ  inscrit ?{" "}
                <span style={{ cursor: "pointer", color: "#4caf50" }} onClick={() => setShowRegister(false)}>
                  Se connecter
                </span>
              </p>
            </form>
          ) : (
            // Formulaire de connexion
            <form className="login-form" onSubmit={async (e) => {
              e.preventDefault();
              setLoginError('');
              try {
                const res = await fetch('http://localhost:5000/api/v1/users/login', {
                  method: 'POST',
                  headers: { 'Content-Type': 'application/json' },
                  body: JSON.stringify(loginData)
                });
                const data = await res.json();
                if (res.ok) {
                  alert('Connexion rÃ©ussie !');
                  // Stocke le token si tu veux : localStorage.setItem('token', data.token)
                } else {
                  setLoginError(data.message || "Erreur de connexion");
                }
              } catch {
                setLoginError("Erreur serveur");
              }
            }}>
              <input type="text" required placeholder="adresse e-mail"
                value={loginData.email}
                onChange={e => setLoginData({ ...loginData, email: e.target.value })} />
              <input type="password" required placeholder="mot de passe"
                value={loginData.password}
                onChange={e => setLoginData({ ...loginData, password: e.target.value })} />
              <button>connexion</button>
              {loginError && <div style={{ color: 'red' }}>{loginError}</div>}
              <p className="message">
                Pas encore inscrit ?{" "}
                <span style={{ cursor: "pointer", color: "#4caf50" }} onClick={() => setShowRegister(true)}>
                  CrÃ©er un compte
                </span>
              </p>
            </form>
          )}
          <button onClick={() => navigate('/accueil')}>Retour Ã  l'accueil</button>
        </div>
      </div>
    </div>
  );
}

export default Login;
