import React, { useState } from 'react';
import './Login.css';

function Login() {
  const [showRegister, setShowRegister] = useState(false);

  // Pour afficher un message lors de la crÃ©ation de compte
  const handleRegister = (e) => {
    e.preventDefault();
    alert("ðŸ‘‹ Bienvenue ! Votre compte a Ã©tÃ© crÃ©Ã© avec succÃ¨s !");
    setShowRegister(false);
  };

  // Pour afficher/masquer le mot de passe
  const togglePassword = (e) => {
    const input = e.target.previousSibling;
    input.type = input.type === 'password' ? 'text' : 'password';
    e.target.classList.toggle('fa-eye-slash');
  };

  return (
    <div>
      <div className="welcome-message">
        <p>Chaque connexion commence par</p>
        <p>un sourire et une tasse de thÃ©.</p>
      </div>

      <div className="login-page">
        <div className="form">
          {/* Formulaire d'inscription */}
          {showRegister ? (
            <form className="register-form" onSubmit={handleRegister}>
              <input type="text" required placeholder="nom" />
              <input type="text" required placeholder="prenom" />
              <div style={{ position: "relative", display: "inline-block", width: "100%" }}>
                <input
                  type="password"
                  placeholder="mot de passe"
                  required
                  style={{ paddingRight: "40px", width: "100%" }}
                />
                <i
                  className="fa fa-eye"
                  onClick={togglePassword}
                  style={{
                    position: "absolute",
                    right: 8,
                    top: "50%",
                    transform: "translateY(-50%)",
                    cursor: "pointer",
                    color: "#555"
                  }}
                ></i>
              </div>
              <input type="text" required placeholder="adresse e-mail" />
              <input type="tel" required placeholder="numÃ©ro de tÃ©lÃ©phone" />
              <input type="number" name="codepostal" placeholder="Code postal" required />
              <input type="text" name="adresse" placeholder="Adresse" required />
              <input type="text" name="pays" placeholder="Pays" required />
              <button type="submit">crÃ©er</button>
              <p className="message">
                DÃ©jÃ  inscrit ?{" "}
                <span style={{ cursor: "pointer", color: "#4caf50" }} onClick={() => setShowRegister(false)}>
                  Se connecter
                </span>
              </p>
            </form>
          ) : (
            // Formulaire de connexion
            <form className="login-form" onSubmit={e => e.preventDefault()}>
              <input type="text" required placeholder="nom et prenom d'utilisateur" />
              <div style={{ position: "relative", display: "inline-block", width: "100%" }}>
                <input
                  type="password"
                  placeholder="mot de passe"
                  required
                  style={{ paddingRight: "40px", width: "100%" }}
                />
                <i
                  className="fa fa-eye"
                  onClick={togglePassword}
                  style={{
                    position: "absolute",
                    right: 8,
                    top: "50%",
                    transform: "translateY(-50%)",
                    cursor: "pointer",
                    color: "#555"
                  }}
                ></i>
              </div>
              <button>connexion</button>
              <p className="message">
                Pas encore inscrit ?{" "}
                <span style={{ cursor: "pointer", color: "#4caf50" }} onClick={() => setShowRegister(true)}>
                  CrÃ©er un compte
                </span>
              </p>
            </form>
          )}
        </div>
      </div>
    </div>
  );
}

export default Login;
