import React, { useEffect, useState } from 'react';
import './Categories.css';

function Categories() {
  const [categories, setCategories] = useState([]);
  const [productsByCategory, setProductsByCategory] = useState({});
  const [loading, setLoading] = useState(true);

  // Récupère les catégories
  useEffect(() => {
    fetch('http://localhost:5000/api/v1/categories')
      .then(res => res.json())
      .then(data => {
        setCategories(data);
        setLoading(false);
        // Pour chaque catégorie, récupère ses produits
        data.forEach(cat => {
          fetch(`http://localhost:5000/api/v1/products?categories=${cat._id}`)
            .then(res => res.json())
            .then(products => {
              setProductsByCategory(prev => ({
                ...prev,
                [cat._id]: products
              }));
            });
        });
      });
  }, []);

  if (loading) return <div>Chargement...</div>;

  return (
    <div className="categories-bg">
      <h1>Nos Catégories Solidaires</h1>
      {categories.map(cat => (
        <section className="category" key={cat._id}>
          <h2>{cat.icon ? <span>{cat.icon} </span> : null}{cat.name}</h2>
          <div className="products-row">
            {(productsByCategory[cat._id] || []).map(product => (
              <div className="product" key={product._id}>
                <img
                  src={product.image}
                  alt={product.name}
                  onError={e => { e.target.src = '/logo192.png'; }}
                />
                <h3>{product.name}</h3>
                <p>{product.description}</p>
                <p className="price">Prix : {product.price} DH</p>
                <span className="favori"><i className="fas fa-heart"></i></span>
                <button className="add-to-cart" data-price={product.price}>
                  Donner ce produit
                </button>
              </div>
            ))}
          </div>
        </section>
      ))}
    </div>
  );
}

export default Categories;
